package com.cv.finalprojectmobileapplication;

import android.content.Intent;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

public class ImageFragment extends Fragment {

    ImageView backMainBtn, image;
    TextView desc;
    String imageUrl, descUrl;

    FirebaseDatabase database = FirebaseDatabase.getInstance();
    DatabaseReference ref = database.getReference("imageData");

    View.OnClickListener backMain = new View.OnClickListener() {
        @Override
        public void onClick(View view) {
            Intent toMain = new Intent(requireActivity(), MainActivity.class);
            startActivity(toMain);
        }
    };

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        return inflater.inflate(R.layout.fragment_image, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        backMainBtn = view.findViewById(R.id.backMain_btn);
        backMainBtn.setOnClickListener(backMain);
        image = view.findViewById(R.id.image_image);
        desc = view.findViewById(R.id.desc_image);
        imageUrl = "data:image/png;base64,";
        descUrl = "This is the logo of Android.";
        Glide.with(requireActivity()).load(imageUrl).into(image);
        desc.setText(descUrl);

    }
}